import React from 'react';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = { summary: "" };
    this.updateSummary = this.updateSummary.bind(this);
  }

  updateSummary() {
    this.setState({summary: "handled summary"});
  }

  render() {
    return (
      <div className="App">
        <header>
         <h1>Skimmer</h1>
        </header>

         <form>
          <p>
          <input type="text" input="Input" placeholder="Input"/>
          </p>
          <p>
           <button onClick={this.updateSummary}>
            Submit
          </button>
          </p>
        </form>

        <p>Summarized Text: </p>
        {this.state.summary}
      </div>
    );
  }
}

export default App;
